customElements.define("joomla-editor-none",class extends HTMLElement{connectedCallback(){this.insertAtCursor=this.insertAtCursor.bind(this);const e=this;Joomla.editors.instances[e.childNodes[0].id]={id:e.childNodes[0].id,element:e,getValue:()=>e.childNodes[0].value,setValue:s=>e.childNodes[0].value=s,replaceSelection:s=>e.insertAtCursor(s),onSave:()=>{}}}disconnectedCallback(){delete Joomla.editors.instances[this.childNodes[0].id]}insertAtCursor(e){if(this.childNodes[0].selectionStart||0===this.childNodes[0].selectionStart){const s=this.childNodes[0].selectionStart,t=this.childNodes[0].selectionEnd;this.childNodes[0].value=this.childNodes[0].value.substring(0,s)+e+this.childNodes[0].value.substring(t,this.childNodes[0].value.length)}else this.childNodes[0].value+=e}});